mapper.select:
select count(*)
from subscribe
where member_idx = #{member_idx}

select q_money 
from member
where member_idx = #{member_idx}

mapper.update:
update member
set q_money = q_money - #{cost}
where member_idx = #{member_idx}

update subscribe
set subscribe_end = #{subscribe_end}
where subscribe_idx = #{subscribe_idx}
and member_idx = #{member_idx}
